mapper.select:
WHERE LOWER(name) LIKE LOWER(CONCAT('%', #{keyword}, '%'))
WHERE LOWER(username) LIKE LOWER(CONCAT('%', #{keyword}, '%'))
ORDER BY id DESC LIMIT #{from}, #{rows}

mapper.update:
UPDATE s1_user
SET password = #{password}, phoneNM = #{phoneNM}, email = #{email}
WHERE id = #{id}

UPDATE s1_user
SET password = #{newPassword}
WHERE username = #{username}

UPDATE s1_user
SET phoneNM = #{newPhoneNumber}
WHERE username = #{username}

UPDATE s1_user
SET email = #{newEmailAddress}
WHERE username = #{username}

UPDATE s1_user
SET status = 1
WHERE username = #{username}

mapper.select:
SELECT id
FROM s1_user
WHERE username = #{username}
